sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  if(inputs.statusCode == '200'){
    var resBody = JSON.parse(inputs.responseBody);
    var res = new consumeCovidAPI(resBody).fetchStates();
    outputs.info = res;
  }
  else{
    outputs.info = inputs.statusCode + " " + inputs.errorMessage;
  }
})(inputs, outputs);

